function AllMeetups() {

    const [isLoading, setIsLoading] = useState(true);
    const [loadedMeetups, setLoadedMeetups] = useState([]);

    if (!isLoading) {
      fetch('https://react-getting-started-1b18d-default-rtdb.asia-southeast1.firebasedatabase.app/meetups.json')
        .then(response => {
          return response.json();
        }).then(data => {
          setIsLoading(false)
          setLoadedMeetups(data)
        });
    }

      if (isLoading) {
        return <section>
          <p>Loading..</p>
        </section>
      }

    return (
      <section>
        <h1>All Meetups</h1>
        <MeetupList meetups={loadedMeetups} />

      </section>
    );
}

export default AllMeetups;
